import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { Container, Typography, List, ListItem, ListItemText, Button, TextField, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';

function ConsultantDashboard() {
  const { user } = useContext(AuthContext);
  const [consultations, setConsultations] = useState([]);
  const [selectedConsultation, setSelectedConsultation] = useState(null);
  const [notes, setNotes] = useState('');

  useEffect(() => {
    fetchConsultations();
  }, []);

  const fetchConsultations = async () => {
    const response = await fetch('/api/consultations', {
      headers: { Authorization: localStorage.getItem('token') },
    });
    const data = await response.json();
    setConsultations(data);
  };

  const handleAcceptConsultation = async (id) => {
    const response = await fetch(`/api/consultations/${id}/accept`, {
      method: 'PUT',
      headers: { Authorization: localStorage.getItem('token') },
    });
    if (response.ok) {
      fetchConsultations(); // Refresh the list
    }
  };

  const handleRejectConsultation = async (id) => {
    const response = await fetch(`/api/consultations/${id}/reject`, {
      method: 'PUT',
      headers: { Authorization: localStorage.getItem('token') },
    });
    if (response.ok) {
      fetchConsultations(); // Refresh the list
    }
  };

  const handleAddNotes = async () => {
    const response = await fetch(`/api/consultations/${selectedConsultation._id}/notes`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: localStorage.getItem('token'),
      },
      body: JSON.stringify({ notes }),
    });
    if (response.ok) {
      setSelectedConsultation(null); // Close the dialog
      fetchConsultations(); // Refresh the list
    }
  };

  return (
    <Container>
      <Typography variant="h4" style={{ marginBottom: '20px', color: '#e91e63' }}>
        Welcome, {user ? user.username : 'Consultant'}!
      </Typography>
      <Typography variant="h5" style={{ marginBottom: '20px', color: '#9c27b0' }}>
        All Consultations
      </Typography>
      <List>
        {consultations.map((consultation) => (
          <ListItem key={consultation._id}>
            <ListItemText
              primary={`${consultation.studentName} - ${consultation.course}`}
              secondary={`Date: ${new Date(consultation.consultationDate).toLocaleString()}`}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={() => handleAcceptConsultation(consultation._id)}
            >
              Accept
            </Button>
            <Button
              variant="contained"
              color="secondary"
              onClick={() => handleRejectConsultation(consultation._id)}
            >
              Reject
            </Button>
            <Button
              variant="contained"
              onClick={() => setSelectedConsultation(consultation)}
            >
              Add Notes
            </Button>
          </ListItem>
        ))}
      </List>

      {/* Add Notes Dialog */}
      <Dialog open={selectedConsultation !== null} onClose={() => setSelectedConsultation(null)}>
        <DialogTitle>Add Notes</DialogTitle>
        <DialogContent>
          <TextField
            label="Notes"
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
            fullWidth
            multiline
            rows={4}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setSelectedConsultation(null)}>Cancel</Button>
          <Button onClick={handleAddNotes} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default ConsultantDashboard;
