import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

function ConsultationsPage() {
  const [consultations, setConsultations] = useState([]);
  const [status, setStatus] = useState("");
  const [reply, setReply] = useState("");
  const [selectedConsultation, setSelectedConsultation] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();

  const fetchConsultations = useCallback(async () => {
    try {
      const storedUser = localStorage.getItem("user");
      if (!storedUser) {
        alert("You are not logged in!");
        navigate("/login");
        return;
      }
      const { token } = JSON.parse(storedUser);

      const res = await axios.get("http://localhost:5000/consultations/all", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (res.data.length === 0) {
        alert("No consultations found!");
      }

      setConsultations(res.data);
    } catch (error) {
      console.error("Failed to fetch consultations:", error);
      alert("Error fetching consultations. Please try again.");
    }
  }, [navigate]);

  useEffect(() => {
    fetchConsultations();
  }, [fetchConsultations]);

  const handleUpdate = async () => {
    try {
      const storedUser = localStorage.getItem("user");
      const { token } = JSON.parse(storedUser);

      const data = { consultationId: selectedConsultation._id, status, reply };

      await axios.put("http://localhost:5000/consultations/update-status-reply", data, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Consultation updated successfully!");
      setIsModalOpen(false);
      fetchConsultations();
    } catch (error) {
      console.error("Error updating consultation:", error);
      alert("Failed to update consultation. Try again!");
    }
  };

  const openModal = (consultation) => {
    setSelectedConsultation(consultation);
    setStatus(consultation.status || "");
    setReply(consultation.reply || "");
    setIsModalOpen(true);
  };

  return (
    <div className="dashboard-container">
      <button className="menu-btn" onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
        ☰
      </button>

      <div className={`sidebar-overlay ${isSidebarOpen ? "show" : ""}`} onClick={() => setIsSidebarOpen(false)}></div>

      <div className={`sidebar ${isSidebarOpen ? "open" : ""}`}>
        <button className="close-btn" onClick={() => setIsSidebarOpen(false)}>✖</button>
        <h4 className="text-center text-light mt-3">Menu</h4>
        <button className="btn btn-secondary w-100 mb-3" onClick={() => navigate("/consultant-dashboard")}>
          Back to Dashboard
        </button>
      </div>

      <div className="container mt-5">
        <h2 className="text-center text-primary">Consultations</h2>

        {consultations.length > 0 ? (
          <ul className="list-group mt-3">
            {consultations.map((consultation) => (
              <li key={consultation._id} className="list-group-item">
                <strong>{consultation.topic}</strong>
                <p>{consultation.description}</p>
                <p><strong>Student:</strong> {consultation.student?.name || "Unknown"}</p>
                <p><strong>Status:</strong> {consultation.status}</p>
                <p><strong>Reply:</strong> {consultation.reply || "No reply yet"}</p>

                <button className="btn btn-success" onClick={() => openModal(consultation)}>
                  Send Reply
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-center text-muted mt-3">No consultation requests available.</p>
        )}
      </div>

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-container">
            <h4>Reply to Consultation</h4>
            <p><strong>Topic:</strong> {selectedConsultation.topic}</p>
            <p><strong>Description:</strong> {selectedConsultation.description}</p>

            <select className="form-control my-2" onChange={(e) => setStatus(e.target.value)} value={status}>
              <option value="">Select Status</option>
              <option value="Pending">Pending</option>
              <option value="Approved">Approved</option>
              <option value="Rejected">Rejected</option>
            </select>

            <textarea
              className="form-control my-2"
              placeholder="Write a reply..."
              value={reply}
              onChange={(e) => setReply(e.target.value)}
            ></textarea>

            <button className="btn btn-success" onClick={handleUpdate}>Submit</button>
            <button className="btn btn-danger" onClick={() => setIsModalOpen(false)}>Cancel</button>
          </div>
        </div>
      )}

      <style>
        {`
          .modal-overlay {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.5);
            display: flex;
            justify-content: center;
            align-items: center;
            z-index: 1000;
          }
          .modal-container {
            background: white;
            padding: 20px;
            border-radius: 10px;
            width: 400px;
            text-align: center;
          }
        `}
      </style>
    </div>
  );
}

export default ConsultationsPage;
