import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import { useAuth } from "../../context/AuthContext";

function ConsultationsPage() {
  const { logout } = useAuth();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [consultations, setConsultations] = useState([]);
  const [expandedConsultation, setExpandedConsultation] = useState(null);
  const [status, setStatus] = useState({});
  const [replies, setReplies] = useState({});
  const navigate = useNavigate();

  // Fetch consultations
  const fetchConsultations = useCallback(async () => {
    try {
      const storedUser = localStorage.getItem("user");
      if (!storedUser) {
        alert("You are not logged in!");
        navigate("/login");
        return;
      }
      const { token } = JSON.parse(storedUser);

      const res = await axios.get("http://localhost:5000/consultations/all", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setConsultations(res.data);
    } catch (error) {
      console.error("Failed to fetch consultations:", error);
      alert("Error fetching consultations.");
    }
  }, [navigate]);

  useEffect(() => {
    fetchConsultations();
  }, [fetchConsultations]);

  const toggleConsultationDetails = (consultationId) => {
    setExpandedConsultation(expandedConsultation === consultationId ? null : consultationId);
  };

  const handleStatusChange = (consultationId, newStatus) => {
    setStatus((prev) => ({ ...prev, [consultationId]: newStatus }));
  };

  const handleReplyChange = (consultationId, newReply) => {
    setReplies((prev) => ({ ...prev, [consultationId]: newReply }));
  };

  const handleUpdate = async (consultationId, recipientType) => {
    try {
      const storedUser = localStorage.getItem("user");
      if (!storedUser) {
        alert("User not authenticated!");
        return;
      }

      const { token } = JSON.parse(storedUser);

      const data = {
        consultationId,
        status: status[consultationId] || "Pending",
        reply: replies[consultationId] || "",
        recipientType,
      };

      await axios.put("http://localhost:5000/consultations/update-status-reply", data, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Consultation updated successfully!");
      fetchConsultations();
    } catch (error) {
      console.error("Error updating consultation:", error);
      alert("Failed to update consultation.");
    }
  };

  const toggleSidebar = () => setIsSidebarOpen(!isSidebarOpen);
  const closeSidebar = () => setIsSidebarOpen(false);
  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <div className="consultations-container">
      {/* Sidebar Toggle Button */}
      <button className="menu-btn" onClick={toggleSidebar}>☰</button>

      {/* Sidebar Overlay */}
      <div className={`sidebar-overlay ${isSidebarOpen ? "show" : ""}`} onClick={closeSidebar}></div>

      {/* Sidebar Navigation */}
      <div className={`sidebar ${isSidebarOpen ? "open" : ""}`}>
        <h4 className="text-center text-light mt-3">📌 Menu</h4>
        <button className="btn btn-primary w-100" onClick={() => navigate("/consultations")}>
          🔍 View Consultations
        </button>
        <button className="btn btn-primary w-100 mt-2" onClick={() => navigate("/consultant-dashboard")}>
          🔙 Back to Dashboard
        </button>
        <div className="logout-container">
          <button className="btn btn-danger w-100" onClick={handleLogout}>
            🚪 Logout
          </button>
        </div>
      </div>

      {/* Consultations List */}
      <div className="content">
        <h2 className="text-center title-text">Consultations</h2>
        {consultations.length > 0 ? (
          <ul className="list-group mt-3">
            {consultations.map((consultation) => (
              <li key={consultation._id} className="list-group-item consultation-item">
                {/* Click to toggle details */}
                <div className="consultation-header" onClick={() => toggleConsultationDetails(consultation._id)}>
                  <strong>{consultation.topic}</strong>
                  <span className="expand-icon">{expandedConsultation === consultation._id ? "▲" : "▼"}</span>
                </div>

                {/* Show details only when expanded */}
                {expandedConsultation === consultation._id && (
                  <div className="consultation-details">
                    <p><strong>Description:</strong> {consultation.description}</p>
                    <p><strong>Student:</strong> {consultation.student?.name || "Unknown"}</p>
                    <p><strong>Status:</strong> {consultation.status}</p>
                    <p><strong>Reply:</strong> {consultation.reply || "No reply yet"}</p>

                    {/* Status Dropdown */}
                    <select className="form-control my-2"
                      value={status[consultation._id] || consultation.status}
                      onChange={(e) => handleStatusChange(consultation._id, e.target.value)}
                    >
                      <option value="Pending">Pending</option>
                      <option value="Approved">Approved</option>
                      <option value="Rejected">Rejected</option>
                    </select>

                    {/* Reply Input Field */}
                    <textarea className="form-control my-2"
                      placeholder="Write a reply..."
                      value={replies[consultation._id] || ""}
                      onChange={(e) => handleReplyChange(consultation._id, e.target.value)}
                    ></textarea>

                    {/* Update Button */}
                    <button className="btn btn-success" onClick={() => handleUpdate(consultation._id, consultation.recipientType)}>
                      ✅ Update Status & Reply
                    </button>
                  </div>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-center text-muted mt-3">No consultation requests available.</p>
        )}
      </div>

      {/* Styles */}
      <style>
        {`
          * {
            box-sizing: border-box;
            margin: 0;
            padding: 0;
            font-family: 'Poppins', sans-serif;
          }

          .consultations-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            height: 100vh;
            padding: 50px;
            background: #121212;
            color: white;
          }

          .title-text {
            font-size: 32px;
            font-weight: bold;
            color: #007bff;
            text-shadow: 2px 2px 10px rgba(0, 123, 255, 0.5);
          }

          .menu-btn {
            position: fixed;
            top: 15px;
            left: 15px;
            background: #1b1f23;
            color: white;
            border: none;
            padding: 12px 18px;
            font-size: 22px;
            cursor: pointer;
            border-radius: 8px;
            z-index: 1100;
          }

          .sidebar {
            position: fixed;
            top: 0;
            left: -260px;
            width: 260px;
            height: 100vh;
            background: rgba(0, 0, 0, 0.8);
            padding: 20px;
            transition: left 0.3s ease-in-out;
          }
          .sidebar.open {
            left: 0;
          }

          .consultation-item {
            background: rgba(255, 255, 255, 0.1);
            border-radius: 8px;
            padding: 10px;
            margin-bottom: 10px;
            cursor: pointer;
          }

          .consultation-details {
            background: rgba(0, 0, 0, 0.5);
            padding: 15px;
            border-radius: 8px;
          }
        `}
      </style>
    </div>
  );
}

export default ConsultationsPage;
