import React, { useState, useEffect } from "react";
import axios from "axios";


function StudentDashboard() {
    const [topic, setTopic] = useState("");
    const [description, setDescription] = useState("");
    const [consultations, setConsultations] = useState([]);

    useEffect(() => {
        fetchConsultations();
    }, []);

    const fetchConsultations = async () => {
        try {
            const token = localStorage.getItem("token");
            const res = await axios.get("http://localhost:5000/api/consultations/student", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setConsultations(res.data);
        } catch (error) {
            console.error("Failed to fetch consultations:", error);
        }
    };

    const handleCreate = async () => {
      try {
        const token = localStorage.getItem("token"); // ✅ Get token from storage
        console.log("Token before API request:", token); // ✅ Debugging
    
        if (!token) {
          alert("Unauthorized: Please log in again.");
          return;
        }
    
        const response = await axios.post(
          "http://localhost:5000/api/consultations/create",
          { topic, description },
          { headers: { Authorization: `Bearer ${token}` } } // ✅ Send token in request headers
        );
    
        alert("Consultation request created!");
      } catch (error) {
        console.error("Failed to create consultation:", error);
        alert(error.response?.data?.message || "Error creating consultation. Try again!");
      }
    };
    
    
    
    return (
        <div>
            <h2>Student Dashboard</h2>
            <input placeholder="Topic" onChange={(e) => setTopic(e.target.value)} />
            <input placeholder="Description" onChange={(e) => setDescription(e.target.value)} />
            <button onClick={handleCreate}>Request Consultation</button>

            <h3>Your Consultations:</h3>
            <ul>
                {consultations.map((consultation) => (
                    <li key={consultation._id}>{consultation.topic} - {consultation.status}</li>
                ))}
            </ul>
        </div>
    );
}

export default StudentDashboard;
