import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../../context/AuthContext";

function StudentDashboard() {
  const { logout } = useAuth();
  const [userRole, setUserRole] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    consultations: 0,
    pendingRequests: 0,
    approvedConsultations: 0
  });

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const { role } = JSON.parse(storedUser);
      setUserRole(role);
    }
  }, []);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const closeSidebar = () => {
    setIsSidebarOpen(false);
  };

  const handleLogout = () => {
    logout();
    navigate("/login");
  };
  // ✅ Fetch live consultation statistics
  const fetchStatistics = useCallback(async () => {
    try {
      const storedUser = localStorage.getItem("user");
      if (!storedUser) {
        alert("You are not logged in!");
        navigate("/login");
        return;
      }
      const { token } = JSON.parse(storedUser);

      const response = await axios.get("http://localhost:5000/consultations/student-stats", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setStats({
        consultations: response.data.totalConsultations || 0,
        pendingRequests: response.data.pendingRequests || 0,
        approvedConsultations: response.data.approvedConsultations || 0
      });
    } catch (error) {
      console.error("Failed to fetch statistics:", error);
    }
  }, [navigate]);

  // ✅ Fetch stats on mount and update every 10 seconds
  useEffect(() => {
    fetchStatistics();
    const interval = setInterval(fetchStatistics, 10000); // Auto-update every 10s
    return () => clearInterval(interval); // Cleanup interval on unmount
  }, [fetchStatistics]);

  return (
    <div className="dashboard-container">
      {/* Sidebar Toggle Button */}
      <button className="menu-btn" onClick={toggleSidebar}>☰</button>

      {/* Sidebar Overlay */}
      {isSidebarOpen && <div className="sidebar-overlay" onClick={closeSidebar}></div>}

      {/* Sidebar Navigation */}
      <div className={`sidebar ${isSidebarOpen ? "open" : ""}`}>
        <h3 className="text-center text-light mt-3">Student Panel</h3>
        <button className="btn btn-primary w-100" onClick={() => navigate("/request-consultation")}>
          Request Consultation
        </button>
        <button className="btn btn-primary w-100 mt-2" onClick={() => navigate("/student-consultations")}>
          My Consultations
        </button>
        <button className="btn btn-primary w-100 mt-2" onClick={() => navigate("/request-teacher-consultation")}>
          Teacher Consultation
        </button>
        <button className="btn btn-primary w-100 mt-2" onClick={() => navigate("/student-profile")}>
          My Profile
        </button>
        <button className="btn btn-primary w-100 mt-2" onClick={() => navigate("/student-dashboard")}>
          Back to Dashboard
        </button>
        <div className="logout-container">
          <button className="btn btn-danger w-100" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      {/* Dashboard Main Content */}
      <div className="container mt-5">
        <h2 className="text-center text-success">Welcome, Student!</h2>
        <p className="text-center text-muted">Manage your consultations, requests, and profile from here.</p>

        {/* 🔹 Student Statistics Section */}
        <div className="row mt-4">
          <div className="col-md-4">
            <div className="dashboard-card">
              <h4>📑 Consultations</h4>
              <p className="count">8</p>
            </div>
          </div>
          <div className="col-md-4">
            <div className="dashboard-card">
              <h4>📝 Pending Requests</h4>
              <p className="count">3</p>
            </div>
          </div>
          <div className="col-md-4">
            <div className="dashboard-card">
              <h4>✔ Approved Consultations</h4>
              <p className="count">5</p>
            </div>
          </div>
        </div>
      </div>

      {/* 🔹 Sidebar & Dashboard Styles */}
      <style>
        {`
          /* Full Page Styling */
          html, body {
            margin: 0;
            padding: 0;
            width: 100%;
            height: 100%;
            overflow-x: hidden;
            background: #f8f9fa;
          }

          .dashboard-container {
            position: relative;
            min-height: 100vh;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            transition: all 0.3s ease-in-out;
          }

          /* Sidebar */
          .sidebar {
            position: fixed;
            top: 0;
            left: -270px;
            width: 270px;
            height: 100%;
            background: #343a40;
            padding: 20px;
            transition: left 0.3s ease-in-out;
            z-index: 1000;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            box-shadow: 4px 0 10px rgba(0, 0, 0, 0.3);
          }
          .sidebar.open {
            left: 0;
          }

          .sidebar-overlay {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.5);
            z-index: 999;
            display: none;
          }
          .sidebar-overlay.show {
            display: block;
          }

          .logout-container {
            margin-top: auto;
          }

          /* Sidebar Buttons */
          .btn {
            transition: all 0.3s ease-in-out;
            border-radius: 8px;
            padding: 10px;
            font-weight: bold;
          }
          .btn:hover {
            filter: brightness(90%);
            transform: scale(1.05);
          }

          /* Sidebar Toggle Button */
          .menu-btn {
            position: fixed;
            top: 10px;
            left: 15px;
            background: #0066cc;
            color: white;
            border: none;
            padding: 10px 15px;
            font-size: 18px;
            cursor: pointer;
            border-radius: 5px;
            z-index: 1100;
            transition: all 0.3s ease-in-out;
          }
          .menu-btn:hover {
            background: #004999;
            transform: scale(1.1);
          }

          /* Dashboard Cards */
          .dashboard-card {
            background: white;
            padding: 20px;
            border-radius: 10px;
            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
            text-align: center;
            transition: transform 0.3s ease-in-out;
          }
          .dashboard-card:hover {
            transform: translateY(-5px);
          }
          .dashboard-card h4 {
            font-size: 18px;
            font-weight: 600;
            color: #333;
          }
          .dashboard-card .count {
            font-size: 24px;
            font-weight: bold;
            color: #0066cc;
            margin-top: 10px;
          }

          /* Role Card */
          .role-card {
            background: #fff;
            padding: 15px;
            border-radius: 10px;
            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
            text-align: center;
            margin-top: 30px;
            max-width: 300px;
            margin-left: auto;
            margin-right: auto;
          }

          /* Responsive Fix */
          @media (max-width: 768px) {
            .sidebar {
              width: 100%;
            }
          }
        `}
      </style>
    </div>
  );
}

export default StudentDashboard;
