import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function ConsultantDashboard() {
    const [requests, setRequests] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchConsultations();
    }, []);

    const fetchConsultations = async () => {
        try {
            const storedUser = localStorage.getItem("user");
            if (!storedUser) {
                alert("You are not logged in!");
                navigate("/login");
                return;
            }

            const user = JSON.parse(storedUser);
            const token = user.token; // Ensure token exists

            const res = await axios.get("http://localhost:5000/consultations/all", {
                headers: { Authorization: `Bearer ${token}` },
            });

            setRequests(res.data);
        } catch (error) {
            console.error("Failed to fetch consultations:", error);
            alert("Error fetching consultations. Please try again.");
        }
    };

    return (
        <div className="container mt-5">
            <h2>Consultant Dashboard</h2>
            <ul>
                {requests.length > 0 ? (
                    requests.map((req) => (
                        <li key={req._id}>
                            {req.student?.username || "Unknown Student"} - {req.topic} - {req.description} ({req.status})
                        </li>
                    ))
                ) : (
                    <p>No consultation requests available.</p>
                )}
            </ul>
        </div>
    );
}

export default ConsultantDashboard;
