import React from "react";
import { Link } from "react-router-dom";

const Home = () => {
  return (
    <div className="home-container">
      <div className="welcome-box">
        <h1 className="title">Welcome to the Consultation App</h1>
        <p className="subtitle">
          Easily connect with consultants for your academic and professional needs.
        </p>
        
        <div className="button-group">
          <Link to="/login">
            <button className="btn-primary">Login</button>
          </Link>
          <Link to="/signup">
            <button className="btn-outline">Signup</button>
          </Link>
        </div>
      </div>

      <style>
        {`
          body {
            background:rgb(0, 0, 0);
            color: white;
            margin: 0;
            padding: 0;
            overflow-x: hidden;
          }
          /* Background Image */
          .home-container {
            margin: 0;
            padding: 0;
            display: flex;
            align-items: center;
            justify-content: center;
            height: 100vh;
            text-align: center;
            background: url('https://source.unsplash.com/1600x900/?technology,learning') no-repeat center center/cover;
            position: relative;
          }

          /* Overlay */
          .home-container::before {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            background: rgba(0, 0, 0, 0.5);
            z-index: 1;
          }

          /* Welcome Box */
          .welcome-box {
            background: rgba(255, 255, 255, 0.1);
            backdrop-filter: blur(10px);
            border-radius: 15px;
            padding: 40px;
            z-index: 2;
            box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.3);
          }

          /* Title */
          .title {
            font-size: 2.5rem;
            color: #ffffff;
            font-weight: bold;
            margin-bottom: 15px;
          }

          /* Subtitle */
          .subtitle {
            font-size: 1.2rem;
            color: #f1f1f1;
            margin-bottom: 30px;
          }

          /* Buttons */
          .button-group {
            display: flex;
            gap: 15px;
            justify-content: center;
          }

          .btn-primary {
            background: #007bff;
            color: white;
            border: none;
            padding: 12px 20px;
            font-size: 16px;
            border-radius: 8px;
            cursor: pointer;
            transition: all 0.3s ease;
          }

          .btn-primary:hover {
            background: #0056b3;
            transform: scale(1.05);
          }

          .btn-outline {
            background: transparent;
            border: 2px solid #ffffff;
            color: white;
            padding: 12px 20px;
            font-size: 16px;
            border-radius: 8px;
            cursor: pointer;
            transition: all 0.3s ease;
          }

          .btn-outline:hover {
            background: white;
            color: black;
            transform: scale(1.05);
          }
        `}
      </style>
    </div>
  );
};

export default Home;
