import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function ConsultantDashboard() {
  const [userRole, setUserRole] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const { role } = JSON.parse(storedUser);
      setUserRole(role);
    }
  }, []);

  const toggleSidebar = () => setIsSidebarOpen(!isSidebarOpen);
  const closeSidebar = () => setIsSidebarOpen(false);

  return (
    <div className="dashboard-container">
      {/* Sidebar Toggle Button */}
      <button className="menu-btn" onClick={toggleSidebar}>☰</button>

      {/* Sidebar & Overlay */}
      {isSidebarOpen && <div className="sidebar-overlay show" onClick={closeSidebar}></div>}
      <div className={`sidebar ${isSidebarOpen ? "open" : ""}`}>
        <div className="sidebar-header">
          <h3 className="text-light">Consultant Panel</h3>
          <button className="close-btn" onClick={closeSidebar}>×</button>
        </div>

        {/* Consultant Menu Options */}
        {userRole === "Consultant" && (
          <button className="btn btn-light-blue w-100" onClick={() => navigate("/consultations")}>
            🔍 View Consultations
          </button>
        )}
        {userRole === "Teacher" && (
          <button className="btn btn-light-blue w-100 mt-2" onClick={() => navigate("/teacher-consultations")}>
            📖 View Teacher Consultations
          </button>
        )}

        {/* Logout Button */}
        <div className="logout-container">
          <button className="btn btn-danger w-100" onClick={() => navigate("/login")}>
            Logout
          </button>
        </div>
      </div>

      {/* Welcome Message */}
      <div className="dashboard-content">
        <h2 className="title-text">Consultant Dashboard</h2>
        <p className="subtitle-text">Manage consultations and student requests.</p>
      </div>

      {/* 🔹 Updated Styles */}
      <style>
        {`
          /* General Styling */
          * {
            box-sizing: border-box;
            margin: 0;
            padding: 0;
            font-family: 'Poppins', sans-serif;
          }

          .dashboard-container {
            position: relative;
            background: #121212;
            min-height: 100vh;
            padding-top: 20px;
            color: white;
          }

          /* Sidebar */
          .sidebar {
            position: fixed;
            top: 0;
            left: -270px;
            width: 270px;
            height: 100%;
            background: #1e1e1e;
            padding: 20px;
            transition: left 0.3s ease-in-out;
            z-index: 1000;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            box-shadow: 4px 0 10px rgba(0, 0, 0, 0.3);
          }
          .sidebar.open {
            left: 0;
          }

          .sidebar-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
          }
          .close-btn {
            background: none;
            border: none;
            color: white;
            font-size: 24px;
            cursor: pointer;
          }

          /* Sidebar Overlay */
          .sidebar-overlay {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.5);
            z-index: 999;
            display: none;
          }
          .sidebar-overlay.show {
            display: block;
          }

          /* Sidebar Buttons */
          .btn {
            transition: all 0.3s ease-in-out;
            border-radius: 10px;
            padding: 12px;
            font-weight: bold;
            font-size: 16px;
          }
          .btn-light-blue {
            background: #007BFF;
            color: white;
            border: none;
          }
          .btn-light-blue:hover {
            background: #0056b3;
          }
          .btn-danger {
            background: #d9534f;
            border: none;
          }
          .btn-danger:hover {
            background: #b52b27;
          }

          /* Sidebar Toggle Button */
          .menu-btn {
            position: fixed;
            top: 10px;
            left: 15px;
            background: #1e1e1e;
            color: white;
            border: none;
            padding: 10px 15px;
            font-size: 18px;
            cursor: pointer;
            border-radius: 5px;
            z-index: 1100;
            transition: all 0.3s ease-in-out;
          }
          .menu-btn:hover {
            background: #333;
          }

          /* Dashboard Content */
          .dashboard-content {
            padding: 50px;
            text-align: center;
            width: 100%;
            max-width: 800px;
          }

          .title-text {
            font-size: 32px;
            font-weight: bold;
            color: #007bff;
            text-shadow: 2px 2px 10px rgba(0, 123, 255, 0.5);
          }

          .subtitle-text {
            font-size: 18px;
            color: #ddd;
            margin-top: 10px;
          }

          /* Responsive Design */
          @media (max-width: 768px) {
            .sidebar {
              width: 100%;
            }
          }
        `}
      </style>
    </div>
  );
}

export default ConsultantDashboard;
