import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider, useAuth } from "./context/AuthContext";
import Home from "./pages/Home";
import Login from "./components/Login";
import Signup from "./components/Signup";
import StudentDashboard from "./pages/StudentDashboard";
import ConsultantDashboard from "./pages/ConsultantDashboard";
import Navbar from "./components/Navbar";
import ErrorBoundary from "./ErrorBoundary";

// ✅ Move ProtectedRoute inside App so it has access to AuthProvider
function App() {
  function ProtectedRoute({ element, allowedRoles }) {
    const { user } = useAuth();

    if (!user) {
      return <Navigate to="/login" replace />;
    }

    if (!allowedRoles.includes(user.role)) {
      return <Navigate to="/" replace />;
    }

    return element;
  }

  return (
    <ErrorBoundary>
      <AuthProvider>
        <Router>
          <Navbar />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />

            {/* Role-Based Protected Routes */}
            <Route path="/student" element={<ProtectedRoute element={<StudentDashboard />} allowedRoles={["student"]} />} />
            <Route path="/consultant" element={<ProtectedRoute element={<ConsultantDashboard />} allowedRoles={["consultant"]} />} />
          </Routes>
        </Router>
      </AuthProvider>
    </ErrorBoundary>
  );
}

export default App;
