import React from "react";
import { Link } from "react-router-dom";

const Home = () => {
  return (
    <div className="home-container">
      <div className="welcome-box">
        <h1 className="title">Welcome to the Consultation App</h1>
        <p className="subtitle">
          Easily connect with consultants for your academic and professional needs.
        </p>

        <div className="button-group">
          <Link to="/login">
            <button className="btn-primary">Login</button>
          </Link>
          <Link to="/signup">
            <button className="btn-outline">Signup</button>
          </Link>
        </div>
      </div>

      {/* Styled JSX */}
      <style jsx>{`
        @import url("https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;600&display=swap");

        * {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
          font-family: "Poppins", sans-serif;
        }

        .home-container {
          width: 100vw;
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          text-align: center;
          background: url("https://images.pexels.com/photos/2049422/pexels-photo-2049422.jpeg?auto=compress&cs=tinysrgb&w=1920") 
                      no-repeat center center/cover;
          position: relative;
        }

        .home-container::before {
          content: "";
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: linear-gradient(to right, rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.3));
        }

        .welcome-box {
          background: rgba(255, 255, 255, 0.1);
          backdrop-filter: blur(12px);
          border-radius: 12px;
          padding: 40px;
          z-index: 2;
          width: 400px;
          box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.3);
          animation: fadeIn 1.5s ease-in-out;
        }

        .title {
          font-size: 2.5rem;
          color: #ffffff;
          font-weight: 600;
          margin-bottom: 15px;
          text-transform: uppercase;
          letter-spacing: 1px;
        }

        .subtitle {
          font-size: 1.1rem;
          color: #f1f1f1;
          margin-bottom: 30px;
        }

        .button-group {
          display: flex;
          gap: 15px;
          justify-content: center;
        }

        .btn-primary {
          background: linear-gradient(135deg, #007bff, #00d4ff);
          color: white;
          border: none;
          padding: 12px 20px;
          font-size: 16px;
          border-radius: 8px;
          cursor: pointer;
          transition: all 0.3s ease;
          box-shadow: 0 4px 10px rgba(0, 123, 255, 0.2);
        }

        .btn-primary:hover {
          background: linear-gradient(135deg, #0056b3, #008cff);
          transform: scale(1.05);
          box-shadow: 0 4px 15px rgba(0, 123, 255, 0.3);
        }

        .btn-outline {
          background: transparent;
          border: 2px solid white;
          color: white;
          padding: 12px 20px;
          font-size: 16px;
          border-radius: 8px;
          cursor: pointer;
          transition: all 0.3s ease;
        }

        .btn-outline:hover {
          background: white;
          color: black;
          transform: scale(1.05);
        }

        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateY(-20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @media (max-width: 768px) {
          .welcome-box {
            width: 90%;
            padding: 30px;
          }
          .title {
            font-size: 2rem;
          }
          .subtitle {
            font-size: 1rem;
          }
          .button-group {
            flex-direction: column;
          }
        }
      `}</style>
    </div>
  );
};

export default Home;
