import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../context/AuthContext"; // ✅ Import useAuth hook

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();
    const { login } = useAuth(); // ✅ Get login function from AuthContext

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await axios.post("http://localhost:5000/login", { email, password });

            if (response.data.success) {
                alert("Login successful!");
                login(response.data); // ✅ Set user state globally

                // ✅ Redirect user based on role
                if (response.data.role === "Student") {
                    navigate("/student-dashboard");
                } else if (response.data.role === "Consultant") {
                    navigate("/consultant-dashboard");
                } else {
                    navigate("/home"); // Default fallback
                }
            } else {
                alert(response.data.message);
            }
        } catch (error) {
            console.error("Login Error:", error);
            alert("Something went wrong. Please try again.");
        }
    };

    return (
        <div className="d-flex justify-content-center align-items-center text-center vh-100"
             style={{ backgroundImage: "linear-gradient(#00d5ff,#0095ff,rgba(93,0,255,.555))" }}>
            <div className="bg-white p-3 rounded" style={{ width: "40%" }}>
                <h2 className="mb-3 text-primary">Login</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3 text-start">
                        <label className="form-label"><strong>Email</strong></label>
                        <input 
                            type="email" 
                            placeholder="Enter Email"
                            className="form-control" 
                            onChange={(event) => setEmail(event.target.value)}
                            required
                        /> 
                    </div>
                    <div className="mb-3 text-start">
                        <label className="form-label"><strong>Password</strong></label>
                        <input 
                            type="password" 
                            placeholder="Enter Password"
                            className="form-control" 
                            onChange={(event) => setPassword(event.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="btn btn-primary">Login</button>
                </form>

                <p className="container my-2">Don't have an account?</p>
                <Link to="/signup" className="btn btn-secondary">Sign Up</Link>
            </div>
        </div>
    );
};

export default Login;
