import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext"; 

const Navbar = () => {
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate("/login");
    };

    return (
        <nav className="navbar navbar-expand-lg custom-navbar">
            <div className="container">
                <Link className="navbar-brand" to="/">Consultation App</Link>
                
                <button 
                    className="navbar-toggler" 
                    type="button" 
                    data-bs-toggle="collapse" 
                    data-bs-target="#navbarNav"
                >
                    <span className="navbar-toggler-icon"></span>
                </button>

                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ms-auto">
                        {user ? (
                            <>
                                <li className="nav-item">
                                    <span className="nav-link welcome-text">Welcome, {user.role}!</span>
                                </li>
                            <div className="logout-container">    
                                <li className="nav-item">
                                    <button className="btn btn-logout ms-2" onClick={handleLogout}>Logout</button>
                                </li>
                            </div>
                            </>
                        ) : (
                            <>
                                <li className="nav-item">
                                    <Link className="nav-link custom-link" to="/login">Login</Link>
                                </li>
                                <li className="nav-item">
                                    <Link className="btn btn-signup ms-2" to="/signup">Sign Up</Link>
                                </li>
                            </>
                        )}
                    </ul>
                </div>
            </div>

            <style>
                {`
                  /* Navbar Styling */
                  .custom-navbar {
                    background: rgba(0, 0, 0, 0);
                    backdrop-filter: blur(10px);
                    padding: 10px;
                    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.3);
                  }
                  
                  .navbar-brand {
                    font-size: 1.5rem;
                    font-weight: bold;
                    color: white !important;
                    transition: color 0.3s ease;
                  }
                  .navbar-brand:hover {
                    color: #00aaff !important;
                  }

                  /* Navbar Links */
                  .custom-link {
                    color: white !important;
                    transition: color 0.3s ease;
                  }
                  .custom-link:hover {
                    color: #00aaff !important;
                  }
                .ms-2{
                    color:rgb(0,0,0);    
                }
                  /* Welcome Text */
                  .welcome-text {
                    color: #f1f1f1;
                    font-weight: 500;
                  }

                  /* Buttons */
                  .btn-signup {
                    background: #00aaff;
                    color: white;
                    padding: 8px 16px;
                    border-radius: 8px;
                    transition: all 0.3s ease;
                  }
                  .btn-signup:hover {
                    background: #0088cc;
                    transform: scale(1.05);
                  }

                  .btn-logout {
                    background: #ff4444;
                    color: white;
                    padding: 8px 16px;
                    border-radius: 8px;
                    transition: all 0.3s ease;
                  }
                  .btn-logout:hover {
                    background: #cc0000;
                    transform: scale(1.05);
                  }

                  /* Responsive Design */
                  @media (max-width: 768px) {
                    .navbar-nav {
                      text-align: center;
                    }
                    .btn-signup,
                    .btn-logout {
                      width: 100%;
                      margin-top: 5px;
                      color:red;
                    }
                  }
                `}
            </style>
        </nav>
    );
};

export default Navbar;
