import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { Button, Row, Col, Container } from 'react-bootstrap';
import { useSocket } from "../context/SocketContext";
import axios from 'axios';

const Home = () => {
  const { user, logout } = useAuth();
  const { socket } = useSocket();
  const [consultations, setConsultations] = useState([]);

  useEffect(() => {
    if (user?.role === 'student') {
      axios
        .get(`/api/consultations?studentId=${user._id}`)
        .then((response) => setConsultations(response.data))
        .catch((error) => console.error('Error fetching consultations', error));
    }

    if (socket) {
      const handleStatusChange = (updatedConsultation) => {
        setConsultations((prev) =>
          prev.map((consultation) =>
            consultation._id === updatedConsultation._id ? updatedConsultation : consultation
          )
        );
      };

      socket.on('consultationStatusChanged', handleStatusChange);

      return () => {
        socket.off('consultationStatusChanged', handleStatusChange);
      };
    }
  }, [user, socket]);

  return (
    <Container className="mt-5">
      <Row className="justify-content-center">
        <Col md={8} lg={6}>
          <h1 className="text-center mb-4">Welcome to the Student Consultation App</h1>

          {user ? (
            <>
              <h4>Welcome back, {user.name}!</h4>

              {user.role === 'student' && (
                <>
                  <h5>Your Consultations:</h5>
                  {consultations.length > 0 ? (
                    <ul>
                      {consultations.map((consultation) => (
                        <li key={consultation._id}>
                          <Link to={`/consultation/${consultation._id}`}>
                            {consultation.title} - Status: {consultation.status}
                          </Link>
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <p>No consultations available.</p>
                  )}

                  <Button as={Link} to="/consultation/create" variant="primary">
                    Create a New Consultation
                  </Button>
                </>
              )}

              {user.role === 'consultant' && (
                <>
                  <h5>Consultation Requests:</h5>
                  <ul>
                    {/* Example placeholder for consultations awaiting response */}
                    <li>
                      <Link to="/consultation/1">Consultation Request #1</Link>
                    </li>
                  </ul>
                  <Button as={Link} to="/dashboard" variant="primary">
                    Go to Dashboard
                  </Button>
                </>
              )}

              <div className="text-center mt-4">
                <Button as={Link} to="/dashboard" variant="primary" className="me-2">
                  Go to Dashboard
                </Button>
                <Button onClick={logout} variant="danger">
                  Log Out
                </Button>
              </div>
            </>
          ) : (
            <>
              <h5>To get started, please log in or register.</h5>
              <div className="text-center mt-4">
                <Button as={Link} to="/login" variant="primary" className="me-2">
                  Log In
                </Button>
                <Button as={Link} to="/register" variant="secondary">
                  Register
                </Button>
              </div>
            </>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default Home;
