import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function StudentDashboard() {
    const [topic, setTopic] = useState("");
    const [description, setDescription] = useState("");
    const [consultations, setConsultations] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchConsultations();
    }, []);

    const fetchConsultations = async () => {
        try {
            const storedUser = localStorage.getItem("user");
            if (!storedUser) {
                alert("You are not logged in!");
                navigate("/login");
                return;
            }

            const user = JSON.parse(storedUser);
            const token = user.token; // ✅ Get correct token

            const res = await axios.get("http://localhost:5000/consultations/student", {
                headers: { Authorization: `Bearer ${token}` },
            });

            setConsultations(res.data);
        } catch (error) {
            console.error("Failed to fetch consultations:", error);
            alert("Error fetching consultations. Please try again.");
        }
    };

    const handleCreate = async () => {
      try {
          const storedUser = localStorage.getItem("user");
          if (!storedUser) {
              alert("Unauthorized: Please log in again.");
              navigate("/login");
              return;
          }
  
          const user = JSON.parse(storedUser);
          const token = user.token; // ✅ Get correct token
  
          console.log("Sending API request with token:", token); // ✅ Debugging
  
          const response = await axios.post(
              "http://localhost:5000/consultations/create",
              { topic, description },
              { headers: { Authorization: `Bearer ${token}` } }
          );
  
          alert("Consultation request created!");
          setTopic("");  // ✅ Clear input fields
          setDescription("");
          fetchConsultations();  // ✅ Refresh the list
      } catch (error) {
          console.error("Failed to create consultation:", error);
          alert(error.response?.data?.error || "Error creating consultation. Try again!");
      }
  };
  
    return (
        <div className="container mt-5">
            <h2>Student Dashboard</h2>

            <div className="mb-3">
                <input
                    className="form-control"
                    placeholder="Topic"
                    value={topic}
                    onChange={(e) => setTopic(e.target.value)}
                />
            </div>

            <div className="mb-3">
                <input
                    className="form-control"
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
            </div>

            <button className="btn btn-primary" onClick={handleCreate}>Request Consultation</button>

            <h3 className="mt-4">Your Consultations:</h3>
            {consultations.length > 0 ? (
                <ul className="list-group">
                    {consultations.map((consultation) => (
                        <li className="list-group-item" key={consultation._id}>
                            {consultation.topic} - {consultation.status}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No consultation requests found.</p>
            )}
        </div>
    );
}

export default StudentDashboard;
