import React, { useState, useEffect } from "react"; // ✅ Added useEffect import
import { useNavigate } from "react-router-dom";

function StudentDashboard() {
 // const [userRole, setUserRole] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const { role } = JSON.parse(storedUser);
      setUserRole(role);
    }
  }, []);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const closeSidebar = () => {
    setIsSidebarOpen(false);
  };

  return (
    <div className="dashboard-container">
      {/* Top-left menu button */}
      <button className="menu-btn" onClick={toggleSidebar}>
        ☰ Open Menu
      </button>

      {/* Sidebar Overlay (Closes sidebar when clicked) */}
      {isSidebarOpen && (
        <div className="sidebar-overlay" onClick={closeSidebar}></div>
      )}

      {/* Sidebar Navigation */}
      <div className={`sidebar ${isSidebarOpen ? "open" : ""}`}>
        <button className="close-btn" onClick={closeSidebar}>
          ✖
        </button>
        <h4 className="text-center text-light mt-3">Menu</h4>
        <button className="btn btn-primary w-100 mb-2" onClick={() => navigate("/request-consultation")}>
          Request Consultation
        </button>
        <button className="btn btn-secondary w-100" onClick={() => navigate("/student-consultations")}>
          View My Consultations
        </button>
        <button className="btn btn-secondary w-100 mt-2" onClick={() => navigate("/request-teacher-consultation")}>
          Request Teacher Consultation
        </button>
      </div>

      {/* Welcome Message */}
      <h2 className="text-center text-success mt-5">Student Dashboard</h2>
      <p className="mt-4 text-center text-muted">
        Welcome to your dashboard! Use the menu to navigate.
      </p>

      {/* Styles */}
      <style>
        {`
          /* Dashboard Container */
          .dashboard-container {
            position: relative;
          }

          /* Top-left menu button */
          .menu-btn {
            position: absolute;
            top: 10px; 
            left: 10px;
            background:rgb(45, 141, 219);
            color: white;
            border: none;
            padding: 10px 15px;
            font-size: 18px;
            cursor: pointer;
            border-radius: 5px;
          }
          .menu-btn:hover {
            background: #495057;
          }

          /* Sidebar */
          .sidebar {
            position: fixed;
            top: 0;
            left: -250px;
            width: 250px;
            height: 100%;
            background: #343a40;
            padding: 20px;
            transition: left 0.3s ease;
            z-index: 1000;
          }
          .sidebar.open {
            left: 0;
          }

          /* Sidebar Overlay */
          .sidebar-overlay {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.5);
            z-index: 999;
          }

          /* Close Button */
          .close-btn {
            background: none;
            border: none;
            font-size: 24px;
            color: white;
            cursor: pointer;
            position: absolute;
            top: 10px;
            right: 15px;
          }
        `}
      </style>
    </div>
  );
}

export default StudentDashboard;
