import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import { Link } from "react-router-dom";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [role, setRole] = useState("Student"); // ✅ Default role is "Student"
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await axios.post('http://localhost:5000/register', { name, email, password, role });

            if (response.data.success) {
                alert(response.data.message);
                navigate('/login');
            } else {
                alert(response.data.message);
            }
        } catch (error) {
            console.error("Signup Error:", error);
            alert("Something went wrong. Please try again.");
        }
    };

    return (
        <div>
            <div className="d-flex justify-content-center align-items-center text-center vh-100" 
                 style={{backgroundImage : "linear-gradient(#00d5ff,#0095ff,rgba(93,0,255,.555))"}}>
                <div className="bg-white p-3 rounded" style={{width : '40%'}}>
                    <h2 className='mb-3 text-primary'>Sign Up</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3 text-start">
                            <label className="form-label"><strong>Name</strong></label>
                            <input 
                                type="text"
                                placeholder="Enter Name"
                                className="form-control" 
                                onChange={(event) => setName(event.target.value)}
                                required
                            /> 
                        </div>
                        <div className="mb-3 text-start">
                            <label className="form-label"><strong>Email</strong></label>
                            <input 
                                type="email" 
                                placeholder="Enter Email"
                                className="form-control" 
                                onChange={(event) => setEmail(event.target.value)}
                                required
                            /> 
                        </div>
                        <div className="mb-3 text-start">
                            <label className="form-label"><strong>Password</strong></label>
                            <input 
                                type="password" 
                                placeholder="Enter Password"
                                className="form-control" 
                                onChange={(event) => setPassword(event.target.value)}
                                required
                            />
                        </div>
                        <div className="mb-3 text-start">
                            <label className="form-label"><strong>Role</strong></label>
                            <select 
                                className="form-control"
                                onChange={(event) => setRole(event.target.value)}
                                required
                            >
                                <option value="Student">Student</option>
                                <option value="Consultant">Consultant</option>
                            </select>
                        </div>
                        <button type="submit" className="btn btn-primary">Sign Up</button>
                    </form>

                    <p className='container my-2'>Already have an account?</p>
                    <Link to='/login' className="btn btn-secondary">Login</Link>
                </div>
            </div>
        </div>
    )
}

export default Signup;
