import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function TeacherConsultations() {
  const [consultations, setConsultations] = useState([]);
  const [status, setStatus] = useState("");
  const [reply, setReply] = useState("");
  const navigate = useNavigate();

  const fetchConsultations = useCallback(async () => {
    try {
      const storedUser = localStorage.getItem("user");
      if (!storedUser) {
        alert("You are not logged in!");
        navigate("/login");
        return;
      }
      const { token } = JSON.parse(storedUser);

      const res = await axios.get("http://localhost:5000/consultations/teacher", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setConsultations(res.data);
    } catch (error) {
      console.error("Failed to fetch consultations:", error);
      alert("Error fetching consultations. Please try again.");
    }
  }, [navigate]);

  useEffect(() => {
    fetchConsultations();
  }, [fetchConsultations]);

  // ✅ Add the missing handleUpdate function
  const handleUpdate = async (consultationId) => {
    try {
      const storedUser = localStorage.getItem("user");
      const { token } = JSON.parse(storedUser);

      await axios.put(
        "http://localhost:5000/consultations/update-status-reply",
        { consultationId, status, reply },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("Consultation updated successfully!");
      fetchConsultations(); // ✅ Refresh the list after updating
    } catch (error) {
      console.error("Error updating consultation:", error);
      alert("Failed to update consultation. Try again!");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center text-primary">Teacher Consultations</h2>
      <button className="btn btn-secondary mb-4" onClick={() => navigate("/teacher-dashboard")}>
        Back to Dashboard
      </button>

      {consultations.length > 0 ? (
        <ul className="list-group mt-3">
          {consultations.map((consultation) => (
            <li key={consultation._id} className="list-group-item">
              <strong>{consultation.topic}</strong>
              <p>{consultation.description}</p>
              <p className="text-muted">Requested by: {consultation.student?.name || "Unknown"}</p>
              <p><strong>Status:</strong> {consultation.status}</p>
              <p><strong>Reply:</strong> {consultation.reply || "No reply yet"}</p>

              <select className="form-control my-2" onChange={(e) => setStatus(e.target.value)}>
                <option value="">Select Status</option>
                <option value="Pending">Pending</option>
                <option value="Approved">Approved</option>
                <option value="Rejected">Rejected</option>
              </select>

              <textarea
                className="form-control my-2"
                placeholder="Write a reply..."
                value={reply}
                onChange={(e) => setReply(e.target.value)}
              ></textarea>

              <button className="btn btn-success" onClick={() => handleUpdate(consultation._id)}>
                Update Status & Send Reply
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-center text-muted mt-3">No consultation requests available.</p>
      )}
    </div>
  );
}

export default TeacherConsultations;
