import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function ConsultantDashboard() {
  const [userRole, setUserRole] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const { role } = JSON.parse(storedUser);
      setUserRole(role);
    }
  }, []);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar Toggle Button */}
      <button className="menu-btn" onClick={toggleSidebar}>
        ☰
      </button>

      {/* Sidebar Navigation */}
      <div className={`sidebar ${isSidebarOpen ? "open" : ""}`}>
        <button className="close-btn" onClick={toggleSidebar}>×</button>
        <h4 className="sidebar-title">Menu</h4>

        {userRole === "Consultant" && (
          <button className="btn primary-btn" onClick={() => navigate("/consultations")}>
            View Consultations
          </button>
        )}

        {userRole === "Teacher" && (
          <button className="btn secondary-btn" onClick={() => navigate("/teacher-consultations")}>
            View Teacher Consultations
          </button>
        )}
      </div>

      {/* Welcome Message */}
      <div className="dashboard-content">
        <h2 className="dashboard-title">Consultant Dashboard</h2>
        <p className="dashboard-text">Welcome! Use the menu to navigate.</p>
      </div>

      {/* Styles */}
      <style>
        {`
          /* General Styles */
          html, body {
            margin: 0;
            padding: 0;
            width: 100%;
            height: 100%;
            background: #f0f2f5;
            font-family: 'Arial', sans-serif;
          }

          /* Dashboard Container */
          .dashboard-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            height: 100vh;
            transition: all 0.3s ease-in-out;
          }

          /* Sidebar */
          .sidebar {
            position: fixed;
            top: 0;
            left: -280px;
            width: 280px;
            height: 100vh;
            background: rgba(0, 0, 0, 0.9);
            backdrop-filter: blur(10px);
            padding: 20px;
            transition: left 0.4s ease-in-out;
            z-index: 1000;
            box-shadow: 4px 0 15px rgba(0, 0, 0, 0.3);
          }
          .sidebar.open {
            left: 0;
          }

          .sidebar-title {
            color: #fff;
            text-align: center;
            font-size: 1.5rem;
            margin-bottom: 20px;
          }

          /* Buttons */
          .btn {
            display: block;
            width: 100%;
            padding: 12px;
            font-size: 16px;
            font-weight: bold;
            border-radius: 6px;
            transition: 0.3s;
            cursor: pointer;
            margin-bottom: 10px;
          }
          .primary-btn {
            background: #28a745;
            color: white;
            border: none;
          }
          .primary-btn:hover {
            background: #218838;
          }

          .secondary-btn {
            background: #ffc107;
            color: black;
            border: none;
          }
          .secondary-btn:hover {
            background: #e0a800;
          }

          /* Close Button */
          .close-btn {
            background: none;
            border: none;
            font-size: 28px;
            color: white;
            cursor: pointer;
            position: absolute;
            top: 10px;
            right: 15px;
          }
          .close-btn:hover {
            color: #ff4757;
          }

          /* Sidebar Toggle Button */
          .menu-btn {
            position: fixed;
            top: 20px;
            left: 20px;
            background: #343a40;
            color: white;
            border: none;
            padding: 12px 18px;
            font-size: 18px;
            cursor: pointer;
            border-radius: 50px;
            z-index: 1100;
            transition: 0.3s;
          }
          .menu-btn:hover {
            background: #212529;
            transform: scale(1.1);
          }

          /* Main Dashboard Content */
          .dashboard-content {
            text-align: center;
            padding: 40px;
            background: white;
            border-radius: 10px;
            box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
          }
          .dashboard-title {
            color: #28a745;
          }
          .dashboard-text {
            color: #6c757d;
          }

          /* Responsive Design */
          @media (max-width: 768px) {
            .sidebar {
              width: 100%;
            }
            .menu-btn {
              top: 15px;
              left: 15px;
            }
          }
        `}
      </style>
    </div>
  );
}

export default ConsultantDashboard;
