import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { Container, Typography,List, ListItem, ListItemText } from '@material-ui/core';
import ConsultationForm from '../components/consultationform';

function StudentDashboard() {
  const { user } = useContext(AuthContext);
  const [consultations, setConsultations] = useState([]);

  useEffect(() => {
    fetchConsultations();
  }, []);

  const fetchConsultations = async () => {
    const response = await fetch('/api/consultations', {
      headers: { Authorization: localStorage.getItem('token') },
    });
    const data = await response.json();
    setConsultations(data);
  };

  const handleAddConsultation = async (consultation) => {
    const response = await fetch('/api/consultations', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: localStorage.getItem('token'),
      },
      body: JSON.stringify(consultation),
    });
    const data = await response.json();
    setConsultations([...consultations, data]);
  };

  return (
    <Container>
      <Typography variant="h4" style={{ marginBottom: '20px', color: '#4caf50' }}>
        Welcome, {user ? user.username : 'Student'}!
      </Typography>
      <ConsultationForm onAddConsultation={handleAddConsultation} />
      <Typography variant="h5" style={{ marginTop: '20px', color: '#ff9800' }}>
        Your Consultations
      </Typography>
        <List>
          {consultations.map((consultation) => (
            <ListItem key={consultation._id}>
              <ListItemText />
            </ListItem>
          ))}
        </List>
        </Container>
  );
}

export default StudentDashboard;
