import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { Container, Typography, List, ListItem, ListItemText } from '@material-ui/core';

function ConsultantDashboard() {
  const { user } = useContext(AuthContext);
  const [consultations, setConsultations] = useState([]);

  useEffect(() => {
    fetchConsultations();
  }, []);

  const fetchConsultations = async () => {
    const response = await fetch('/api/consultations', {
      headers: { Authorization: localStorage.getItem('token') },
    });
    const data = await response.json();
    setConsultations(data);
  };

  return (
    <Container>
      <Typography variant="h4" style={{ marginBottom: '20px', color: '#e91e63' }}>
        Welcome, {user ? user.username : 'Consultant'}!
      </Typography>
      <Typography variant="h5" style={{ marginBottom: '20px', color: '#9c27b0' }}>
        All Consultations
      </Typography>
      <List>
        {consultations.map((consultation) => (
          <ListItem key={consultation._id}>
            <ListItemText
              primary={`${consultation.studentName} - ${consultation.course}`}
              secondary={`Date: ${new Date(consultation.consultationDate).toLocaleString()}`}
            />
          </ListItem>
        ))}
      </List>
    </Container>
  );
}

export default ConsultantDashboard;
