import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function ConsultantDashboard() {
  const [userRole, setUserRole] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const { role } = JSON.parse(storedUser);
      setUserRole(role);
    }
  }, []);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const closeSidebar = () => {
    setIsSidebarOpen(false);
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar Toggle Button */}
      <button className="menu-btn" onClick={toggleSidebar}>
        ☰
      </button>

      {/* Sidebar Overlay */}
      <div className={`sidebar-overlay ${isSidebarOpen ? "show" : ""}`} onClick={closeSidebar}></div>

      {/* Sidebar Navigation */}
      <div className={`sidebar ${isSidebarOpen ? "open" : ""}`}>
        <h4 className="text-center text-light mt-3">📌 Menu</h4>

        {/* Button for Consultants */}
        {userRole === "Consultant" && (
          <button className="btn btn-primary w-100" onClick={() => navigate("/consultations")}>
            🔍 View Consultations
          </button>
        )}

        {/* Button for Teachers */}
        {userRole === "Teacher" && (
          <button className="btn btn-warning w-100 mt-2" onClick={() => navigate("/teacher-consultations")}>
            📖 View Teacher Consultations
          </button>
        )}
      </div>

      {/* Welcome Message */}
      <div className="dashboard-content">
        <h2 className="text-center title-text">Consultant Dashboard</h2>
        <p className="text-center subtitle-text">Welcome! Use the menu to navigate.</p>
      </div>

      {/* Styles */}
      <style>
        {`
          /* General Styling */
          * {
            box-sizing: border-box;
            margin: 0;
            padding: 0;
            font-family: 'Poppins', sans-serif;
          }

          body {
            background: #0d1117;
            color: white;
            overflow-x: hidden;
          }

          .dashboard-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            height: 100vh;
          }

          /* Sidebar */
          .sidebar {
            position: fixed;
            top: 0;
            left: -260px;
            width: 260px;
            height: 100vh;
            background: rgba(0, 0, 50, 0.8);
            backdrop-filter: blur(8px);
            padding: 20px;
            transition: left 0.3s ease-in-out;
            z-index: 1000;
            box-shadow: 4px 0 10px rgba(0, 0, 0, 0.3);
            border-right: 2px solid rgba(255, 255, 255, 0.1);
          }
          .sidebar.open {
            left: 0;
          }

          /* Sidebar Overlay */
          .sidebar-overlay {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0, 0, 0, 0.5);
            z-index: 999;
            display: none;
          }
          .sidebar-overlay.show {
            display: block;
          }

          /* Sidebar Buttons */
          .btn {
            transition: all 0.3s ease-in-out;
            border-radius: 10px;
            padding: 12px;
            font-weight: bold;
            margin-bottom: 10px;
            font-size: 16px;
          }
          .btn-primary {
            background: #007bff;
            border: none;
          }
          .btn-warning {
            background: #f39c12;
            border: none;
          }
          .btn:hover {
            transform: scale(1.1);
            box-shadow: 0px 4px 10px rgba(255, 255, 255, 0.2);
          }

          /* Sidebar Toggle Button */
          .menu-btn {
            position: fixed;
            top: 15px;
            left: 15px;
            background: #1b1f23;
            color: white;
            border: none;
            padding: 12px 18px;
            font-size: 22px;
            cursor: pointer;
            border-radius: 8px;
            z-index: 1100;
            transition: all 0.3s ease-in-out;
          }
          .menu-btn:hover {
            background: #0056b3;
            transform: scale(1.1);
          }

          /* Welcome Text */
          .dashboard-content {
            padding: 50px;
            text-align: center;
            width: 100%;
            max-width: 800px;
          }

          .title-text {
            font-size: 32px;
            font-weight: bold;
            color: #007bff;
            text-shadow: 2px 2px 10px rgba(0, 123, 255, 0.5);
          }

          .subtitle-text {
            font-size: 18px;
            color: #ddd;
            margin-top: 10px;
          }

          /* Responsive Design */
          @media (max-width: 768px) {
            .sidebar {
              width: 100%;
            }
          }
        `}
      </style>
    </div>
  );
}

export default ConsultantDashboard;
