import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../context/AuthContext";

function TeacherConsultations() {
  const { logout } = useAuth();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [consultations, setConsultations] = useState([]);
  const [expandedConsultation, setExpandedConsultation] = useState(null);
  const [status, setStatus] = useState({});
  const [replies, setReplies] = useState({});
  const navigate = useNavigate();

  const fetchConsultations = useCallback(async () => {
    try {
      const storedUser = localStorage.getItem("user");
      if (!storedUser) {
        alert("Unauthorized: Please log in again.");
        navigate("/login");
        return;
      }
      const { token } = JSON.parse(storedUser);

      const res = await axios.get("http://localhost:5000/consultations/teacher-consultations", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setConsultations(res.data);
    } catch (error) {
      console.error(error.response?.data?.message || "Error fetching consultations.");
      alert("Error fetching consultations. Try again!");
    }
  }, [navigate]);

  useEffect(() => {
    fetchConsultations();
  }, [fetchConsultations]);

  const toggleConsultationDetails = (consultationId) => {
    setExpandedConsultation(expandedConsultation === consultationId ? null : consultationId);
  };

  const handleStatusChange = (consultationId, newStatus) => {
    setStatus((prev) => ({ ...prev, [consultationId]: newStatus }));
  };

  const handleReplyChange = (consultationId, newReply) => {
    setReplies((prev) => ({ ...prev, [consultationId]: newReply }));
  };

  const handleUpdate = async (consultationId) => {
    try {
      const storedUser = localStorage.getItem("user");
      const { token } = JSON.parse(storedUser);

      const data = {
        consultationId,
        status: status[consultationId] || "Pending",
        reply: replies[consultationId] || "",
      };

      await axios.put("http://localhost:5000/consultations/update-status-reply", data, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Consultation updated successfully!");
      fetchConsultations();
    } catch (error) {
      console.error("Error updating consultation:", error.response?.data || error);
      alert("Failed to update consultation. Try again!");
    }
  };

  const toggleSidebar = () => setIsSidebarOpen(!isSidebarOpen);
  const closeSidebar = () => setIsSidebarOpen(false);
  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className={`sidebar ${isSidebarOpen ? "open" : ""}`}>
        <h3 className="sidebar-title">Teacher Panel</h3>
        <button className="btn-primary" onClick={() => navigate("/teacher-dashboard")}>Dashboard</button>
        <button className="btn-primary" onClick={() => navigate("/teacher-consultations")}>Consultations</button>
        <button className="btn-danger logout-btn" onClick={handleLogout}>Logout</button>
      </div>

      {/* Sidebar Toggle Button */}
      <button className="menu-btn" onClick={toggleSidebar}>☰</button>

      <div className="content">
        <h2 className="text-center">Teacher Consultations</h2>
        {consultations.length > 0 ? (
          <ul className="consultation-list">
            {consultations.map((consultation) => (
              <li key={consultation._id} className="consultation-card">
                <div className="card-header" onClick={() => toggleConsultationDetails(consultation._id)}>
                  <strong>{consultation.topic}</strong>
                  <span>{expandedConsultation === consultation._id ? "▲" : "▼"}</span>
                </div>

                {expandedConsultation === consultation._id && (
                  <div className="card-body">
                    <p><strong>Description:</strong> {consultation.description}</p>
                    <p><strong>Student:</strong> {consultation.student?.name || "Unknown"}</p>
                    <p><strong>Status:</strong> {consultation.status}</p>
                    <p><strong>Reply:</strong> {consultation.reply || "No reply yet"}</p>

                    <select
                      className="form-control"
                      value={status[consultation._id] || consultation.status}
                      onChange={(e) => handleStatusChange(consultation._id, e.target.value)}
                    >
                      <option value="Pending">Pending</option>
                      <option value="Approved">Approved</option>
                      <option value="Rejected">Rejected</option>
                    </select>

                    <textarea
                      className="form-control"
                      placeholder="Write a reply..."
                      value={replies[consultation._id] || ""}
                      onChange={(e) => handleReplyChange(consultation._id, e.target.value)}
                    ></textarea>

                    <button className="btn-update" onClick={() => handleUpdate(consultation._id)}>
                      Update Status & Send Reply
                    </button>
                  </div>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-center">No consultations available.</p>
        )}
      </div>

      {/* Styles */}
      <style>
        {`
          body {
            background: #1e1e1e;
            color: white;
          }

          .dashboard-container {
            display: flex;
          }

          .sidebar {
            width: 250px;
            height: 100vh;
            background: #252525;
            padding: 20px;
            position: fixed;
            left: -250px;
            transition: 0.3s ease;
          }

          .sidebar.open {
            left: 0;
          }

          .sidebar-title {
            color: white;
            font-size: 20px;
            margin-bottom: 20px;
          }

          .btn-primary, .btn-danger {
            display: block;
            width: 100%;
            padding: 10px;
            margin-bottom: 10px;
            background: #007bff;
            color: white;
            border: none;
            cursor: pointer;
          }

          .btn-danger {
            background: red;
          }

          .menu-btn {
            background: #007bff;
            color: white;
            padding: 10px 15px;
            font-size: 20px;
            border: none;
            cursor: pointer;
            position: fixed;
            top: 10px;
            left: 10px;
          }

          .content {
            margin-left: 250px;
            padding: 20px;
          }

          .consultation-list {
            list-style: none;
            padding: 0;
          }

          .consultation-card {
            background: #333;
            padding: 15px;
            border-radius: 5px;
            margin-bottom: 10px;
          }

          .card-header {
            display: flex;
            justify-content: space-between;
            cursor: pointer;
          }

          .card-body {
            margin-top: 10px;
          }

          .btn-update {
            background: green;
            color: white;
            padding: 8px;
            border: none;
            cursor: pointer;
          }

          @media (max-width: 768px) {
            .content {
              margin-left: 0;
            }
          }
        `}
      </style>
    </div>
  );
}

export default TeacherConsultations;
