import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const Navbar = () => {
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    // ✅ Load user data properly on mount
    useEffect(() => {
        const storedUser = localStorage.getItem("user");

        try {
            if (storedUser) {
                const parsedUser = JSON.parse(storedUser);
                if (parsedUser && parsedUser.role) {
                    setUser(parsedUser); // ✅ Only set user if valid data exists
                } else {
                    localStorage.removeItem("user"); // ✅ Remove invalid data
                    setUser(null);
                }
            } else {
                setUser(null);
            }
        } catch (error) {
            console.error("Error parsing user data:", error);
            localStorage.removeItem("user"); // ✅ Clear corrupted data
            setUser(null);
        }
    }, []);

    // ✅ Logout Function
    const handleLogout = () => {
        localStorage.removeItem("user"); // ✅ Remove user data
        setUser(null); // ✅ Reset state
        navigate('/login'); // ✅ Redirect to login page
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container">
                <Link className="navbar-brand" to="/">Consultation App</Link>
                <div className="collapse navbar-collapse">
                    <ul className="navbar-nav ms-auto">
                        {user ? (
                            // ✅ Show Logout & Welcome message ONLY if user is valid
                            <>
                                <li className="nav-item">
                                    <span className="nav-link">Welcome, {user.role}!</span>
                                </li>
                                <li className="nav-item">
                                    <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
                                </li>
                            </>
                        ) : (
                            // ✅ Show Login & Signup if no user is logged in
                            <>
                                <li className="nav-item">
                                    <Link className="nav-link" to="/login">Login</Link>
                                </li>
                                <li className="nav-item">
                                    <Link className="nav-link" to="/signup">Sign Up</Link>
                                </li>
                            </>
                        )}
                    </ul>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
